    private void jb_registrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jb_registrarActionPerformed

        
        String nombre="", inventario="", precio="", n_proveedor="", identificacion="";
        
        if(jtxt_nombre.getText().length()==0
                ||jtxt_id.getText().length()==0
                ||jtxt_inventario.getText().length()==0
                ||jtxt_precio_actual.getText().length()==0
                ||jtxt_n_proveedor.getText().length()==0
                ){
            
                JOptionPane.showMessageDialog(null, "HAY CAMPOS VACIOS ","ALERTA ",1);
        
                jtxt_nombre.requestFocus();
                
        }else {
        
        

                nombre=jtxt_nombre.getText();
                identificacion=jtxt_id.getText();
                inventario=jtxt_inventario.getText();
                precio=jtxt_precio_actual.getText();
                n_proveedor=jtxt_n_proveedor.getText();

               conexion con;
                try {
                    con = new conexion();
                    String insert;
                    insert = "insert into producto values ('"+identificacion+"','"+nombre+"','"+inventario+"','"+precio+"','"+n_proveedor+"')";
            
                    
                    con.dml(insert);
                } catch (Exception ex) {
                    
                }
       
        jtxt_nombre.setText("");
        jtxt_id.setText("");
        jtxt_inventario.setText("");
        jtxt_precio_actual.setText("");
        jtxt_n_proveedor.setText("");
        
         jtxt_id.requestFocus();
                
        
        }

    }//GEN-LAST:event_jb_registrarActionPerformed

