    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        try {
            conexion con = new conexion ();
            ResultSet res;
            String id=jtxt_id.getText();
            String Sql="select * from producto where id='"+id+"';";
            res=con.GetConsultar(Sql);
            if(res.next()==true){
                jtxt_nombre.setText(res.getString("Nombre"));
                jtxt_inventario.setText(res.getString("Inventario"));
                jtxt_precio_actual.setText(res.getString("Precio"));
                jtxt_n_proveedor.setText(res.getString("Proveedor"));

                con.Setcerrar();
            }
            else
            {
                JOptionPane.showMessageDialog(null, "Datos erroneos");
            }

        } catch (Exception ex) {
            Logger.getLogger(Cliente.class.getName()).log(Level.SEVERE, null, ex);
        }

    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed

        try {

            conexion con = new conexion();
            String id=jtxt_id.getText();
            String nombre=jtxt_nombre.getText();
            String inventario=jtxt_inventario.getText();
            String precio=jtxt_precio_actual.getText();
            String proveedor=jtxt_n_proveedor.getText();
            String Sql="update producto set Nombre='"+nombre+"', Inventario='"+inventario+"',Precio='"+precio+"',Proveedor='"+proveedor+"' where id='"+id+"';";
            System.out.print(Sql);
            con.dml(Sql);
            JOptionPane.showMessageDialog(null, "Actualizacion con exito " , "FELICIDADES " , JOptionPane.OK_OPTION);
            con.Setcerrar();

        } catch (Exception ex) {
            Logger.getLogger(consultar.class.getName()).log(Level.SEVERE, null, ex);
        }

    }//GEN-LAST:event_jButton2ActionPerformed

